import { useEffect, useState } from "react";
import img from "../testimag.jpeg";

function createGrid() {
  const containerWidth = (70 / 100) * window.innerWidth;
  const containerHeigth = (70 / 100) * window.innerHeight;
  const cellWidth = (containerWidth / 18).toString() + "px";
  const cellHeight = (containerHeigth / 11).toString() + "px";

  console.log(containerWidth, cellWidth, window.innerWidth);
  const cells = [];
  for (let w = 4; w < 238; w++) {
    cells.push(
      <div
        id={w}
        className="cells"
        key={w}
        onClick={(e) => console.log(e.target.id)}
        style={{ width: cellWidth, height: cellHeight }}
      ></div>
    );
  }
  return cells;
}

const Home = () => {
  const [zoom, setZoom] = useState(1);
  const [windowSize, setWindowSize] = useState(window.innerWidth);

  window.addEventListener("resize", () => {
    setWindowSize(window.innerHeight);
  });

  useEffect(() => {}, [windowSize]);

  const handleZoom = (zoomButton) => {
    console.log(zoom);
    if (zoom === 1.75 && zoomButton === "increaseZoom") {
      return;
    }
    if (zoom === 1 && zoomButton === "decreaseZoom") {
      return;
    }
    zoomButton === "increaseZoom"
      ? setZoom((Zoom) => Zoom + 0.25)
      : setZoom((Zoom) => Zoom - 0.25);
  };

  return (
    <div id="home">
      <div
        id="grid-container"
        style={{
          backgroundImage: "url(" + img + ")",
          transform: "scale(" + zoom + ")",
        }}
      >
        {createGrid()}
      </div>
      <button onClick={() => handleZoom("increaseZoom")}>+</button>
      <button onClick={() => handleZoom("decreaseZoom")}>-</button>
    </div>
  );
};

export default Home;
