let medium_1 = [];
let medium_2 = [];
let medium_3 = [];
let hard_1 = [];
let hard_2 = [];
let hard_3 = [];

function setCharacterHitIndicator(element, style) {
  return element.setAttribute("style", style);
}

function checkForWin(board, cellID, character) {
  const clickedCell = document.getElementById(cellID);
  console.log(board);
  if (board === "easy-1" || board === "medium-1") {
    !character
      ? clickedCell.setAttribute(
          "style",
          " height:200%; border:solid red 4px; border-radius:40%; margin-top: -20px"
        )
      : clickedCell.setAttribute(
          "style",
          " height:100%; border:solid red 4px; border-radius:40%;"
        );
    board === "easy-1"
      ? alert("win") /// win for easy 1
      : medium_1.length < 1
      ? medium_1.push(true)
      : alert("win"); /// win for medium 1

    return (clickedCell.id = "");
  } else if (board === "easy-2" || board === "medium-2") {
    clickedCell.setAttribute(
      "style",
      " height:190%; border:solid red 4px; border-radius:40%; margin-top: -6px"
    );
    board === "easy-2"
      ? alert("win") /// win for easy 2
      : medium_2.length < 1
      ? medium_2.push(true)
      : alert("win"); /// win for medium 2

    return (clickedCell.id = "");
  } else if (board === "easy-3" || board === "hard-3") {
    clickedCell.setAttribute(
      "style",
      " height:150%; border:solid red 4px; border-radius:40%; margin-top: -6px; margin-left: 12px; margin-right: -12px"
    );
    board === "easy-3"
      ? alert("win") /// win for easy 3
      : hard_3.length < 2
      ? hard_3.push(true)
      : alert("win"); /// win for hard 3
  } else if (board === "medium-3" || "hard-2") {
    clickedCell.setAttribute(
      "style",
      " height:150%; border:solid red 4px; border-radius:40%; margin-top: -6px; margin-left: -12px; "
    );
    medium_3.length < 1 ? medium_3.push(true) : alert("win");
  }
}

function gamerules(cellid, gameImg) {
  if (!cellid && !gameImg) {
    medium_1 = [];
    medium_2 = [];
    medium_3 = [];
    hard_1 = [];
    hard_2 = [];
    return (hard_3 = []);
  }
  const clickedCell = Number(cellid.split("-")[1]);
  let difficulty;
  const imageNameRegex = /(medium|easy|hard)-\d+/;
  const boardName = gameImg.match(imageNameRegex)[0];
  gameImg.match("easy")
    ? (difficulty = "easy")
    : gameImg.match("medium")
    ? (difficulty = "medium")
    : (difficulty = "hard");

  switch (boardName) {
    case "easy-1":
      if (clickedCell === 310) checkForWin(boardName, cellid, "");
      return;
    case "easy-2":
      if (clickedCell === 265) checkForWin(boardName, cellid, "");
      return;
    case "easy-3":
      if (clickedCell === 591) checkForWin(boardName, cellid, "");
      return;

    case "medium-1":
      if (clickedCell === 261 || clickedCell === 310)
        clickedCell === 261
          ? checkForWin(boardName, cellid, "wizard")
          : checkForWin(boardName, cellid, "");
      return;
    case "medium-2":
      if (clickedCell === 635) checkForWin(boardName, cellid, "wizard");
      return;
    case "medium-3":
      if (clickedCell === 482 || clickedCell === 460);
      return checkForWin(boardName, cellid);

    case "hard-1":
      if (clickedCell === 591) return checkForWin(boardName, cellid);
    case "hard-2":
      if (clickedCell === 591) return checkForWin(boardName, cellid);
    case "hard-3":
      if (clickedCell === 265) return checkForWin(boardName, cellid);
    default:
      return;
  }
}

export default gamerules;
