function checkForWin(board, clickedCell, cellID) {
  if (board.includes("1") && clickedCell === 265) {
    document
      .getElementById(cellID)
      .setAttribute(
        "style",
        " height:200%; border:solid red 4px; border-radius:40%; margin-top: -20px"
      );
  }

  if (board === "easy-2" && clickedCell === 310) {
    document
      .getElementById(cellID)
      .setAttribute(
        "style",
        " height:200%; border:solid red 4px; border-radius:40%; margin-top: -20px"
      );
  }
  if (board === "easy-3" && clickedCell === 591) {
    document
      .getElementById(cellID)
      .setAttribute(
        "style",
        " height:200%; border:solid red 4px; border-radius:40%; margin-top: -20px"
      );
  }
}

function gamerules(cellid, gameImg) {
  const clickedCell = Number(cellid.split("-")[1]);
  let difficulty;
  const imageNameRegex = /(medium|easy|hard)-\d+/;
  const boardName = gameImg.match(imageNameRegex)[0];
  gameImg.match("easy")
    ? (difficulty = "easy")
    : gameImg.match("medium")
    ? (difficulty = "medium")
    : (difficulty = "hard");

  switch (
    clickedCell /// easy 2 waldo
  ) {
    case 265: /// easy-1 waldo
    case 310:
    case 591:
      checkForWin(boardName, clickedCell, cellid);
      return; /// easy-2 waldo
    case 297: /// wizard
    case 261:
      checkForWin(gameImg, cellid);
      return; /// wizard

    case 255: //evil waldo
      difficulty === "hard" ? (
        document
          .getElementById("cell-255")
          .setAttribute("style", " border:solid red 4px; border-radius:40%")
      ) : (
        <></>
      );
      return;
    case 316: ///walda
      difficulty === "hard" ? (
        document
          .getElementById("cell-316")
          .setAttribute(
            "style",
            "border:solid red 4px; border-radius:40%; margin-left:-10px; margin-right:10px; margin-bottom:-10px"
          )
      ) : (
        <></>
      );
  }
  //easy - 3
  switch (
    clickedCell /// easy 3 waldo
  ) {
    case 591: /// easy-3 waldo
      document
        .getElementById("cell-591")
        .setAttribute(
          "style",
          " height:200%; border:solid red 4px; border-radius:40%; margin-top: -10px"
        );
      return;
    case 635: /// wizard
      document
        .getElementById("cell-635")
        .setAttribute(
          "style",
          "border:solid red 4px; border-radius:40%; margin: -15px -5px 5px 5px"
        );
      return;
    case 741: //evil waldo
    case 777:
      document
        .getElementById("cell-777")
        .setAttribute(
          "style",
          " border:solid red 4px; border-radius:40%; margin: -20px 6px 10px -6px"
        );
      return;
    case 483: ///walda
      document
        .getElementById("cell-483")
        .setAttribute(
          "style",
          "border:solid red 4px; border-radius:40%; margin-left:6px; margin-right:-6px; margin-top:-3px"
        );
      return;
  }
  // switch (cell) {
  //   case 384:
  //   case 383:
  //   case 348:
  //   case 349:
  //   case 420:
  //   case 419:
  //   case 456:
  //     alert("found willy ");
  //     document
  //       .getElementById("cell-383")
  //       .setAttribute(
  //         "style",
  //         "grid-column: span 2; grid-row: span 4; border:solid red 4px; border-radius:40%; margin-left: .5rem"
  //       );
  //     return true;
  //   default:
  //     return null;
  // }
}

export default gamerules;
