const medium_1 = [];
const medium_2 = [];
const medium_3 = [];

function checkForWin(board, cellID) {
  const clickedCell = document.getElementById(cellID);
  if (board === "easy-2") {
    clickedCell.setAttribute(
      "style",
      " height:150%; border:solid red 4px; border-radius:40%; margin-top: -10px; margin-left:15px; margin-right:-10px"
    );
    return (clickedCell.id = "");
  }

  if (board === "easy-1" || board === "medium-1") {
    clickedCell.setAttribute(
      "style",
      " height:200%; border:solid red 4px; border-radius:40%; margin-top: -20px"
    );
    if (board === "medium-1") {
      medium_1.length < 2 ? medium_1.push(true) : alert("win");
    }
    return (clickedCell.id = "");
  } else if (board === "easy-3" || board === "medium-3") {
    clickedCell.setAttribute(
      "style",
      " height:190%; border:solid red 4px; border-radius:40%; margin-top: -6px"
    );
    if (board === "medium-3") {
      medium_1.length === 1 ? medium_2.push(true) : alert("win");
    }
    return (clickedCell.id = "");

    // if(board === 'medium-2'){
    //   medium_2.length !== 2 ?
    // }
  }
}

function gamerules(cellid, gameImg) {
  const clickedCell = Number(cellid.split("-")[1]);
  let difficulty;
  const imageNameRegex = /(medium|easy|hard)-\d+/;
  const boardName = gameImg.match(imageNameRegex)[0];
  gameImg.match("easy")
    ? (difficulty = "easy")
    : gameImg.match("medium")
    ? (difficulty = "medium")
    : (difficulty = "hard");

  switch (boardName) {
    case "easy-2":
      if (clickedCell === 265) return checkForWin(boardName, cellid);
    case "easy-1":
      if (clickedCell === 310) return checkForWin(boardName, cellid);
    case "medium-1":
      if (clickedCell === 310 || clickedCell === 297)
        return checkForWin(boardName, cellid);

    case "easy-3":
    case "medium-3":
      if (clickedCell === 591) return checkForWin(boardName, cellid);
  }

  switch (
    clickedCell /// easy 2 waldo
  ) {
    case 297: /// wizard
    case 261:
      checkForWin(gameImg, cellid);
      return; /// wizard

    case 255: //evil waldo
      difficulty === "hard" ? (
        document
          .getElementById("cell-255")
          .setAttribute("style", " border:solid red 4px; border-radius:40%")
      ) : (
        <></>
      );
      return;
    case 316: ///walda
      difficulty === "hard" ? (
        document
          .getElementById("cell-316")
          .setAttribute(
            "style",
            "border:solid red 4px; border-radius:40%; margin-left:-10px; margin-right:10px; margin-bottom:-10px"
          )
      ) : (
        <></>
      );
  }
  //easy - 3
  switch (
    clickedCell /// easy 3 waldo
  ) {
    case 591: /// easy-3 waldo
      document
        .getElementById("cell-591")
        .setAttribute(
          "style",
          " height:200%; border:solid red 4px; border-radius:40%; margin-top: -10px"
        );
      return;
    case 635: /// wizard
      document
        .getElementById("cell-635")
        .setAttribute(
          "style",
          "border:solid red 4px; border-radius:40%; margin: -15px -5px 5px 5px"
        );
      return;
    case 741: //evil waldo
    case 777:
      document
        .getElementById("cell-777")
        .setAttribute(
          "style",
          " border:solid red 4px; border-radius:40%; margin: -20px 6px 10px -6px"
        );
      return;
    case 483: ///walda
      document
        .getElementById("cell-483")
        .setAttribute(
          "style",
          "border:solid red 4px; border-radius:40%; margin-left:6px; margin-right:-6px; margin-top:-3px"
        );
      return;
  }
  // switch (cell) {
