import react, { useState } from "react";
import img from "../testimag.jpeg";

function createGrid() {
  const cells = [];
  for (let w = 4; w < 104; w++) {
    cells.push(
      <div
        id={w}
        className="cells"
        key={w}
        onClick={(e) => console.log(e.target.id)}
      ></div>
    );
  }
  return cells;
}
const Home = () => {
  const [zoom, setZoom] = useState(1);

  const handleZoom = () => {
    console.log(zoom);
    if (zoom === 1 || zoom === 2.25) {
      return;
    }
  };

  return (
    <div id="home">
      <div
        id="grid-container"
        style={{
          backgroundImage: "url(" + img + ")",
          transform: "scale(" + zoom + ")",
        }}
      >
        {createGrid()}
      </div>
      <button onClick={() => setZoom((Zoom) => Zoom + 0.25)}>+</button>
      <button onClick={() => setZoom((Zoom) => Zoom - 0.25)}>-</button>
    </div>
  );
};

export default Home;
